// MODE SELECTION CODE
const introSection = document.querySelector(".introduction");

let activeMode = true;

document.querySelector(".lazyMode").addEventListener("click", () => {
    activeMode = true;
    introSection.classList.add("d-none");
});

document.querySelector(".activeMode").addEventListener("click", () => {
    activeMode = false;
    introSection.classList.add("d-none");
});

document.addEventListener("keydown", (e) => {
    if (activeMode && e.repeat) return;
    addElement();
});

// -----------------------------------------------------------------------------------------------

// ADDING EMOJI AT RANDOM PLACE WITH RANDOM SIZE LOGIC CODE
function addElement(){

    
    const newElement = document.createElement("div");
    
    const randomEmoji = emojis[Math.floor(Math.random() * emojis.length)];
    newElement.textContent = randomEmoji;
    
    const randomSize = Math.floor(Math.random() * 4) + 1; // 1 to 4
    newElement.style.fontSize = `${randomSize}rem`;
    
    newElement.style.position = "absolute";
    newElement.style.top = `${Math.random() * (window.innerHeight - 50)}px`;
    newElement.style.left = `${Math.random() * (window.innerWidth - 50)}px`;
    
    section.appendChild(newElement);
}

// -----------------------------------------------------------------------------------------------

// CAT PARADE AND AUTO SCROLL LOGIC CODE
const startBtn = document.getElementById("startBtn");
const catGif = document.getElementById("catGif");
const scrollContent = document.getElementById("instuction-content");
const instuctioDiv = document.getElementById("instuction-div");

startBtn.addEventListener("click", () => {
    catGif.src = "./img/gifs/Cat Parade.gif";
    startBtn.classList.add("disabled", "opacity-50");
    startBtn.innerHTML = "üêæ Kitties Parade üêæ";


    gsap.to(catGif,{
        x: "940px",
        duration: 180
    })

    gsap.to(instuctioDiv, {
        scrollTop: instuctioDiv.scrollHeight,
        duration: 180, 
        ease: "none"
    });

});

// -----------------------------------------------------------------------------------------------

// INTRODUCTION ANIMATION CODE
const introTL = gsap.timeline();

introTL.from(".introduction", {
    opacity: 0,
    scale: 0.95,
    duration: 1
});

introTL.from("#catGif", {
    x: -150,
    opacity: 0,
    duration: 0.8
});

introTL.from("#startBtn", {
    y: -30,
    opacity: 0,
    duration: 0.6
});

gsap.to("#startBtn", {
    scale: 1.05,
    repeat: -1,
    yoyo: true,
    duration: 0.8,
    ease: "power1.inOut"
});

// -----------------------------------------------------------------------------------------------

document.addEventListener('contextmenu',
function(event){
    event.preventDefault();
})

document.addEventListener("keydown", function (e) {

    const key = e.key.toLowerCase();

    // F12
    if (key === "f12") {
        e.preventDefault();
        return false;
    }

    // Ctrl + U (View Source)
    if (e.ctrlKey && key === "u") {
        e.preventDefault();
        return false;
    }

    // Ctrl + I
    if (e.ctrlKey && key === "i") {
        e.preventDefault();
        return false;
    }

    // Ctrl + Shift + I (DevTools)
    if (e.ctrlKey && e.shiftKey && key === "i") {
        e.preventDefault();
        return false;
    }

    // Ctrl + Shift + J (Console)
    if (e.ctrlKey && e.shiftKey && key === "j") {
        e.preventDefault();
        return false;
    }

    // Ctrl + Shift + C (Inspect)
    if (e.ctrlKey && e.shiftKey && key === "c") {
        e.preventDefault();
        return false;
    }

});
